import React, { useState, useEffect } from "react";
import { Search, MapPin, Mic } from "lucide-react";

// Mock GalleryComponent - replace with your actual component
const GalleryComponent = ({ properties }) => {
  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mt-8">
      {properties.map((property) => (
        <div key={property.id} className="bg-white rounded-lg shadow-md overflow-hidden">
          <img 
            src={`https://bestpropertiesmohali.com/uploads/${property.image}`}
            alt={property.name}
            className="w-full h-48 object-cover"
            onError={(e) => {
              e.target.src = "/api/placeholder/300/200";
            }}
          />
          <div className="p-4">
            <h3 className="text-lg font-semibold text-gray-800">{property.name}</h3>
            <p className="text-blue-600 font-medium">{property.property_type}</p>
            <p className="text-gray-600 flex items-center mt-2">
              <MapPin className="w-4 h-4 mr-1" />
              Sector {property.sector}, {property.address}
            </p>
            <div className="mt-3">
              <p className="text-xl font-bold text-green-600">â‚¹{property.budget}/month</p>
              <p className="text-sm text-gray-500">Security: {property.security_deposite}</p>
            </div>
            <div className="mt-3 flex justify-between text-sm text-gray-600">
              <span>{property.bathrooms} Bathrooms</span>
              <span>Floor {property.floor}</span>
              <span className={property.verified === "Yes" ? "text-green-600" : "text-red-600"}>
                {property.verified === "Yes" ? "Verified" : "Not Verified"}
              </span>
            </div>
            <div className="mt-3">
              <p className="text-sm text-gray-600">{property.property_status}</p>
              {property.amenities && (
                <div className="mt-2">
                  <p className="text-xs text-gray-500">Amenities:</p>
                  <div className="flex flex-wrap gap-1 mt-1">
                    {property.amenities.split('~-~').slice(0, 3).map((amenity, index) => (
                      <span key={index} className="bg-blue-100 text-blue-800 text-xs px-2 py-1 rounded">
                        {amenity}
                      </span>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

// Mock AnimatedText component - replace with your actual component
const AnimatedText = ({ children }) => {
  return <div className="text-2xl font-bold text-gray-800">{children}</div>;
};

export default function Home() {
  const [searchQuery, setSearchQuery] = useState("");
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [recentSearches, setRecentSearches] = useState([
    "Rent in Chandigarh, 1 BHK"
  ]);

  // API configuration
  const API_BASE_URL = "https://bestpropertiesmohali.com";
  const API_TOKEN = "9j1h8hgjO0KUin2bhj58d97jiOh67f5h48hj78hg8vg5j63fo0h930";

  // Fetch properties from API
  const fetchProperties = async (keyword = "") => {
    setLoading(true);
    setError(null);

    try {
      const url = `${API_BASE_URL}/rent-search/?keyword=${encodeURIComponent(keyword)}&sort_by=desc`;

      const response = await fetch(url, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${API_TOKEN}`,
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      setProperties(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error('Error fetching properties:', err);
      setError('Failed to fetch properties. Please try again.');
      // Set mock data for demonstration
      setProperties([
        {
          "id": "38",
          "name": "2bhk,",
          "property_type": "2 BHK",
          "address": "80",
          "sector": "80",
          "budget": "20000",
          "bathrooms": "2",
          "verified": "Yes",
          "floor": "3",
          "security_deposite": "1 Month",
          "property_status": "Semi-Furnished",
          "amenities": "Car parking~-~24x7 Water supply~-~Power Backup~-~Party hall~-~Guest Car Parking~-~Park/Jogging track",
          "image": "2024030712321926.jpg"
        }
      ]);
    } finally {
      setLoading(false);
    }
  };

  // Load initial properties
  useEffect(() => {
    fetchProperties();
  }, []);

  // Handle search
  const handleSearch = () => {
    if (searchQuery.trim()) {
      fetchProperties(searchQuery);
      // Add to recent searches if not already present
      if (!recentSearches.includes(searchQuery)) {
        setRecentSearches(prev => [searchQuery, ...prev.slice(0, 4)]);
      }
    }
  };

  // Handle Enter key press
  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="container px-6 mx-auto mt-5">
        <div className="">
          <section className="">
            {/* Search Bar Section */}
            <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
              {/* Navigation Tabs */}
              <div className="flex space-x-8 mb-6 border-b border-gray-200">
                <button className="pb-3 px-1 border-b-2 border-blue-500 text-blue-600 font-medium">
                  Buy
                </button>
                <button className="pb-3 px-1 text-gray-600 hover:text-gray-800">
                  Rent
                </button>
                <button className="pb-3 px-1 text-gray-600 hover:text-gray-800 relative">
                  New Launch
                  <span className="absolute -top-1 -right-1 w-2 h-2 bg-red-500 rounded-full"></span>
                </button>
                <button className="pb-3 px-1 text-gray-600 hover:text-gray-800">
                  PG / Co-living
                </button>
                <button className="pb-3 px-1 text-gray-600 hover:text-gray-800">
                  Commercial
                </button>
                <button className="pb-3 px-1 text-gray-600 hover:text-gray-800">
                  Plots/Land
                </button>
                <button className="pb-3 px-1 text-gray-600 hover:text-gray-800">
                  Projects
                </button>
              </div>

              {/* Search Input */}
              <div className="flex items-center space-x-4">
                <select className="px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500">
                  <option>All Residential</option>
                  <option>1 BHK</option>
                  <option>2 BHK</option>
                  <option>3 BHK</option>
                </select>

                <div className="flex-1 relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                  <input
                    type="text"
                    placeholder='Search "3 BHK for sale in Mumbai"'
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    onKeyPress={handleKeyPress}
                    className="w-full pl-10 pr-12 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                  <div className="absolute right-3 top-1/2 transform -translate-y-1/2 flex items-center space-x-2">
                    <MapPin className="text-gray-400 w-5 h-5 cursor-pointer hover:text-gray-600" />
                    <Mic className="text-gray-400 w-5 h-5 cursor-pointer hover:text-gray-600" />
                  </div>
                </div>

                <button
                  onClick={handleSearch}
                  disabled={loading}
                  className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 disabled:opacity-50"
                >
                  {loading ? 'Searching...' : 'Search'}
                </button>
              </div>

              {/* Recent Searches */}
              <div className="mt-4 flex items-center space-x-4 text-sm">
                <span className="text-gray-600">Recent searches:</span>
                {recentSearches.map((search, index) => (
                  <button
                    key={index}
                    onClick={() => {
                      setSearchQuery(search);
                      fetchProperties(search);
                    }}
                    className="flex items-center space-x-1 text-blue-600 hover:text-blue-800"
                  >
                    <span>{search}</span>
                  </button>
                ))}
                <button className="text-blue-600 hover:text-blue-800">
                  View all searches
                </button>
              </div>
            </div>

            {/* Content Section */}
            <div className="flex justify-center items-center mb-6">
              <AnimatedText>Property Listings</AnimatedText>
            </div>

            {/* Error Message */}
            {error && (
              <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
                {error}
              </div>
            )}

            {/* Loading State */}
            {loading && (
              <div className="flex justify-center items-center py-12">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
              </div>
            )}

            {/* Properties Grid */}
            {!loading && properties.length > 0 && (
              <GalleryComponent properties={properties} />
            )}

            {/* No Results */}
            {!loading && properties.length === 0 && !error && (
              <div className="text-center py-12">
                <p className="text-gray-600">No properties found. Try a different search term.</p>
              </div>
            )}

            <div>Ayush kuams djwd</div>
          </section>
        </div>
      </div>
    </div>
  );
}
