import React, { useState } from "react";
import GalleryComponent from "./galleryComponent";
import AnimatedText from "./HeadingAnimation";

export default function Home() {
  const [searchParams, setSearchParams] = useState({
    category: "All",
    searchText: "",
    propertyType: "rent"
  });

  const handleInputChange = (field, value) => {
    setSearchParams(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleSearch = () => {
    // Create query parameters for the search
    const queryParams = new URLSearchParams();

    if (searchParams.category && searchParams.category !== "All") {
      queryParams.append("category", searchParams.category);
    }

    if (searchParams.searchText.trim()) {
      queryParams.append("search", searchParams.searchText.trim());
    }

    queryParams.append("type", searchParams.propertyType);

    // Navigate to property page with search parameters using window.location
    const searchUrl = `/property?${queryParams.toString()}`;
    window.location.href = searchUrl;
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  return (
    <div className="">
      <div className="container px-6 mx-auto mt-5">
        {/* Search bar UI */}
        <div className="bg-white shadow-md rounded-lg p-4 mb-6">
          {/* Tab Navigation */}
          {/* <div className="flex space-x-4 border-b border-gray-200 pb-2">
            <button 
              className={`font-semibold px-2 py-1 border-b-2 transition-colors ${
                searchParams.propertyType === 'buy' 
                  ? 'text-blue-600 border-blue-600' 
                  : 'text-gray-600 border-transparent hover:text-blue-600'
              }`}
              onClick={() => handleInputChange('propertyType', 'buy')}
            >
              Buy
            </button>
            <button 
              className={`font-semibold px-2 py-1 border-b-2 transition-colors ${
                searchParams.propertyType === 'rent' 
                  ? 'text-blue-600 border-blue-600' 
                  : 'text-gray-600 border-transparent hover:text-blue-600'
              }`}
              onClick={() => handleInputChange('propertyType', 'rent')}
            >
              Rent
            </button>
            <button 
              className={`font-semibold px-2 py-1 border-b-2 relative transition-colors ${
                searchParams.propertyType === 'new-launch' 
                  ? 'text-blue-600 border-blue-600' 
                  : 'text-gray-600 border-transparent hover:text-blue-600'
              }`}
              onClick={() => handleInputChange('propertyType', 'new-launch')}
            >
              New Launch
              <span className="absolute -top-1 -right-1 h-4 w-4 bg-red-500 rounded-full"></span>
            </button>
            <button 
              className={`font-semibold px-2 py-1 border-b-2 transition-colors ${
                searchParams.propertyType === 'pg-coliving' 
                  ? 'text-blue-600 border-blue-600' 
                  : 'text-gray-600 border-transparent hover:text-blue-600'
              }`}
              onClick={() => handleInputChange('propertyType', 'pg-coliving')}
            >
              PG / Co-living
            </button>
          </div> */}

          {/* Search Bar */}
          <div className="flex items-center mt-4 space-x-3">
            <select 
              className="border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
              value={searchParams.category}
              onChange={(e) => handleInputChange('category', e.target.value)}
            >
              <option value="All">All</option>
              <option value="Residential">Residential</option>
              <option value="Commercial">Commercial</option>
              <option value="Others">Others</option>
            </select>

            <div className="flex-grow relative">
              <input
                type="text"
                placeholder="Search '3 BHK for sale in Mumbai'"
                className="w-full border border-gray-300 rounded-md px-3 py-2 pl-10 focus:outline-none focus:ring-2 focus:ring-blue-500"
                value={searchParams.searchText}
                onChange={(e) => handleInputChange('searchText', e.target.value)}
                onKeyPress={handleKeyPress}
              />
              <span className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400">üîç</span>
            </div>

            <button 
              className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition-colors duration-200"
              onClick={handleSearch}
            >
              Search
            </button>
          </div>
        </div>

        {/* Existing content */}
        <div className="">
          <section className="">
            <div className="flex justify-center items-center">
              {/* You can add AnimatedText component here if needed */}
              {/* <AnimatedText /> */}
            </div>

            <GalleryComponent />
          </section>
        </div>
      </div>
    </div>
  );
}
