import React, { useState, useEffect } from "react";
import GalleryComponent from "./galleryComponent";
import AnimatedText from "./HeadingAnimation";

export default function Home() {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");

  // Fetch initial properties (default search for sector 80)
  useEffect(() => {
    const fetchProperties = async () => {
      try {
        const response = await fetch(
          "https://bestpropertiesmohali.com/rent-search/?keyword=80&sort_by=desc",
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        const data = await response.json();
        setProperties(data.result);
        setLoading(false);
      } catch (err) {
        setError("Failed to fetch properties");
        setLoading(false);
      }
    };

    fetchProperties();
  }, []);

  // Handle search form submission
  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchQuery.trim()) return;

    setLoading(true);
    setError(null);
    try {
      const response = await fetch(
        `https://bestpropertiesmohali.com/rent-search/?keyword=${encodeURIComponent(searchQuery)}&sort_by=desc`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      const data = await response.json();
      setProperties(data.result);
      setLoading(false);
    } catch (err) {
      setError("Failed to fetch search results");
      setLoading(false);
    }
  };

  return (
    <div className="  ">
      <div className="container px-6 mx-auto mt-5 ">
        {/* Adding the search bar UI similar to the image */}
        <div className="bg-white shadow-md rounded-lg p-4 mb-6">
          {/* Tab Navigation */}
          <div className="flex space-x-4 border-b border-gray-200 pb-2">
            {/* <button className="text-blue-600 border-b-2 border-blue-600 font-semibold px-2 py-1">
              Buy
            </button>
            <button className="text-gray-600 font-semibold px-2 py-1 hover:text-blue-600">
              Rent
            </button>
            <button className="text-gray-600 font-semibold px-2 py-1 hover:text-blue-600 relative">
              New Launch
              <span className="absolute -top-1 -right-1 h-4 w-4 bg-red-500 rounded-full"></span>
            </button>
            <button className="text-gray-600 font-semibold px-2 py-1 hover:text-blue-600">
              PG / Co-living
            </button> */}
          </div>
          {/* Search Bar */}
          <form onSubmit={handleSearch} className="flex items-center mt-4 space-x-3">
            <select className="border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500">
              <option>All</option>
              <option>Residential</option>
              <option>Commercial</option>
              <option>Others</option>
            </select>
            <div className="flex-grow relative">
              <input
                type="text"
                placeholder="Search '3 BHK for sale in Mumbai'"
                className="w-full border border-gray-300 rounded-md px-3 py-2 pl-10 focus:outline-none focus:ring-2 focus:ring-blue-500"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
              <span className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400">üîç</span>
            </div>
            <button
              type="submit"
              className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
            >
              Search
            </button>
          </form>
        </div>

        {/* Existing content remains unchanged */}
        <div className="">
          <section className=" ">
            <div className="flex justify-center items-center"></div>
            {loading ? (
              <p>Loading properties...</p>
            ) : error ? (
              <p className="text-red-500">{error}</p>
            ) : (
              <GalleryComponent
                properties={properties}
                imgUrl="https://bestpropertiesmohali.com/assets/properties/"
              />
            )}
          </section>
        </div>
      </div>
    </div>
  );
}

export const liveUrl = "https://bestpropertiesmohali.com/";
export const token = "9j1h8hgjO0KUin2bhj58d97jiOh67f5h48hj78hg8vg5j63fo0h930";
