import React, { useState, useEffect } from "react";
import GalleryComponent from "./galleryComponent";
import AnimatedText from "./HeadingAnimation";

export default function Home() {
  const [searchParams, setSearchParams] = useState({
    category: "All",
    searchText: "",
    propertyType: "rent"
  });

  const [allProperties, setAllProperties] = useState([]);
  const [loading, setLoading] = useState(false);

  // Call API when component mounts
  useEffect(() => {
    fetchProperties();
  }, []);

  const fetchProperties = async () => {
    setLoading(true);
    try {
      // Replace with your actual API endpoint
      const response = await fetch('YOUR_API_ENDPOINT_HERE');
      const data = await response.json();

      if (data.result) {
        setAllProperties(data.result);
      }
    } catch (error) {
      console.error('Error fetching properties:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (field, value) => {
    setSearchParams(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleSearch = () => {
    // Filter properties based on search parameters
    let filteredProperties = [...allProperties];

    // Filter by property type (BHK type)
    if (searchParams.category && searchParams.category !== "All") {
      if (searchParams.category === "Residential") {
        filteredProperties = filteredProperties.filter(property => 
          property.property_type.toLowerCase().includes('bhk') ||
          property.property_type.toLowerCase().includes('apartment') ||
          property.property_type.toLowerCase().includes('flat')
        );
      } else if (searchParams.category === "Commercial") {
        filteredProperties = filteredProperties.filter(property => 
          property.property_type.toLowerCase().includes('commercial') ||
          property.property_type.toLowerCase().includes('office') ||
          property.property_type.toLowerCase().includes('shop')
        );
      }
    }

    // Filter by search text (name, address, sector)
    if (searchParams.searchText.trim()) {
      const searchTerm = searchParams.searchText.toLowerCase().trim();
      filteredProperties = filteredProperties.filter(property => 
        property.name.toLowerCase().includes(searchTerm) ||
        property.address.toLowerCase().includes(searchTerm) ||
        property.sector.toLowerCase().includes(searchTerm) ||
        property.property_type.toLowerCase().includes(searchTerm)
      );
    }

    // Create query parameters for the search
    const queryParams = new URLSearchParams();

    if (searchParams.category && searchParams.category !== "All") {
      queryParams.append("category", searchParams.category);
    }

    if (searchParams.searchText.trim()) {
      queryParams.append("search", searchParams.searchText.trim());
    }

    queryParams.append("type", searchParams.propertyType);

    // Store filtered results in localStorage to pass to property page
    localStorage.setItem('searchResults', JSON.stringify({
      properties: filteredProperties,
      searchParams: searchParams,
      imgUrl: "https://bestpropertiesmohali.com/assets/properties/"
    }));

    // Navigate to property page with search parameters
    const searchUrl = `/property?${queryParams.toString()}`;
    window.location.href = searchUrl;
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  // Get unique property types for filter options
  const getPropertyTypes = () => {
    const types = [...new Set(allProperties.map(prop => prop.property_type))];
    return types;
  };

  // Get unique amenities for filter options
  const getAmenities = () => {
    const allAmenities = allProperties.flatMap(prop => 
      prop.amenities ? prop.amenities.split('~-~') : []
    );
    return [...new Set(allAmenities)];
  };

  return (
    <div className="">
      <div className="container px-6 mx-auto mt-5">
        {/* Search bar UI */}
        <div className="bg-white shadow-md rounded-lg p-4 mb-6">
          {/* Tab Navigation */}
          {/* <div className="flex space-x-4 border-b border-gray-200 pb-2">
            <button 
              className={`font-semibold px-2 py-1 border-b-2 transition-colors ${
                searchParams.propertyType === 'buy' 
                  ? 'text-blue-600 border-blue-600' 
                  : 'text-gray-600 border-transparent hover:text-blue-600'
              }`}
              onClick={() => handleInputChange('propertyType', 'buy')}
            >
              Buy
            </button>
            <button 
              className={`font-semibold px-2 py-1 border-b-2 transition-colors ${
                searchParams.propertyType === 'rent' 
                  ? 'text-blue-600 border-blue-600' 
                  : 'text-gray-600 border-transparent hover:text-blue-600'
              }`}
              onClick={() => handleInputChange('propertyType', 'rent')}
            >
              Rent
            </button>
            <button 
              className={`font-semibold px-2 py-1 border-b-2 relative transition-colors ${
                searchParams.propertyType === 'new-launch' 
                  ? 'text-blue-600 border-blue-600' 
                  : 'text-gray-600 border-transparent hover:text-blue-600'
              }`}
              onClick={() => handleInputChange('propertyType', 'new-launch')}
            >
              New Launch
              <span className="absolute -top-1 -right-1 h-4 w-4 bg-red-500 rounded-full"></span>
            </button>
            <button 
              className={`font-semibold px-2 py-1 border-b-2 transition-colors ${
                searchParams.propertyType === 'pg-coliving' 
                  ? 'text-blue-600 border-blue-600' 
                  : 'text-gray-600 border-transparent hover:text-blue-600'
              }`}
              onClick={() => handleInputChange('propertyType', 'pg-coliving')}
            >
              PG / Co-living
            </button>
          </div> */}

          {/* Search Bar */}
          <div className="flex items-center mt-4 space-x-3">
            <select 
              className="border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
              value={searchParams.category}
              onChange={(e) => handleInputChange('category', e.target.value)}
            >
              <option value="All">All</option>
              <option value="Residential">Residential</option>
              <option value="Commercial">Commercial</option>
              <option value="Others">Others</option>
            </select>

            <div className="flex-grow relative">
              <input
                type="text"
                placeholder="Search '3 BHK for sale in Mumbai'"
                className="w-full border border-gray-300 rounded-md px-3 py-2 pl-10 focus:outline-none focus:ring-2 focus:ring-blue-500"
                value={searchParams.searchText}
                onChange={(e) => handleInputChange('searchText', e.target.value)}
                onKeyPress={handleKeyPress}
              />
              <span className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400">üîç</span>
            </div>

            <button 
              className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition-colors duration-200 disabled:opacity-50"
              onClick={handleSearch}
              disabled={loading}
            >
              {loading ? 'Loading...' : 'Search'}
            </button>
          </div>
        </div>

        {/* Property Type and Amenities Filters (Dynamic from API data) */}
        {allProperties.length > 0 && (
          <div className="bg-white shadow-md rounded-lg p-4 mb-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {/* Property Types */}
              <div>
                <h3 className="font-semibold text-gray-800 mb-3">Property Type</h3>
                <div className="space-y-2">
                  {getPropertyTypes().slice(0, 6).map((type, index) => (
                    <label key={index} className="flex items-center space-x-2 cursor-pointer">
                      <input type="checkbox" className="rounded border-gray-300" />
                      <span className="text-gray-700">{type}</span>
                    </label>
                  ))}
                  {getPropertyTypes().length > 6 && (
                    <button className="text-blue-600 text-sm hover:underline">Show More</button>
                  )}
                </div>
              </div>

              {/* Amenities */}
              <div>
                <h3 className="font-semibold text-gray-800 mb-3">Amenities</h3>
                <div className="space-y-2">
                  {getAmenities().slice(0, 6).map((amenity, index) => (
                    <label key={index} className="flex items-center space-x-2 cursor-pointer">
                      <input type="checkbox" className="rounded border-gray-300" />
                      <span className="text-gray-700">{amenity}</span>
                    </label>
                  ))}
                  {getAmenities().length > 6 && (
                    <button className="text-blue-600 text-sm hover:underline">Show More</button>
                  )}
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Loading State */}
        {loading && (
          <div className="text-center py-8">
            <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
            <p className="mt-2 text-gray-600">Loading properties...</p>
          </div>
        )}

        {/* Existing content */}
        <div className="">
          <section className="">
            <div className="flex justify-center items-center">
              {/* You can add AnimatedText component here if needed */}
              {/* <AnimatedText /> */}
            </div>

            <GalleryComponent />
          </section>
        </div>
      </div>
    </div>
  );
}
