import React, { useState, useEffect } from "react";
import GalleryComponent from "./galleryComponent";
import AnimatedText from "./HeadingAnimation";

export default function Home() {
  // State for search input and API data
  const [searchKeyword, setSearchKeyword] = useState("80"); // Default keyword
  const [sortBy, setSortBy] = useState("desc"); // Default sort order
  const [properties, setProperties] = useState([]); // Store API data
  const [propertyType, setPropertyType] = useState("All"); // Store selected property type

  // Function to fetch data from API
  const fetchProperties = async () => {
    try {
      const response = await fetch(
        `https://bestpropertiesmohali.com/rent-search/?keyword=${searchKeyword}&sort_by=${sortBy}`
      );
      const data = await response.json();
      setProperties(data.result || []); // Update state with fetched properties
    } catch (error) {
      console.error("Error fetching properties:", error);
      setProperties([]); // Set empty array on error
    }
  };

  // Fetch data on component mount and when searchKeyword or sortBy changes
  useEffect(() => {
    fetchProperties();
  }, [searchKeyword, sortBy]);

  // Handle search form submission
  const handleSearch = (e) => {
    e.preventDefault();
    fetchProperties(); // Trigger API call with current searchKeyword and sortBy
  };

  return (
    <div className="">
      <div className="container px-6 mx-auto mt-5">
        {/* Search Bar UI */}
        <div className="bg-white shadow-md rounded-lg p-4 mb-6">
          {/* Tab Navigation (Uncomment and modify as needed) */}
          {/* <div className="flex space-x-4 border-b border-gray-200 pb-2">
            <button className="text-blue-600 border-b-2 border-blue-600 font-semibold px-2 py-1">
              Buy
            </button>
            <button className="text-gray-600 font-semibold px-2 py-1 hover:text-blue-600">
              Rent
            </button>
            <button className="text-gray-600 font-semibold px-2 py-1 hover:text-blue-600 relative">
              New Launch
              <span className="absolute -top-1 -right-1 h-4 w-4 bg-red-500 rounded-full"></span>
            </button>
            <button className="text-gray-600 font-semibold px-2 py-1 hover:text-blue-600">
              PG / Co-living
            </button>
          </div> */}
          {/* Search Bar */}
          <form onSubmit={handleSearch} className="flex items-center mt-4 space-x-3">
            <select
              value={propertyType}
              onChange={(e) => setPropertyType(e.target.value)}
              className="border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="All">All</option>
              <option value="Residential">Residential</option>
              <option value="Commercial">Commercial</option>
              <option value="Others">Others</option>
            </select>
            <div className="flex-grow relative">
              <input
                type="text"
                value={searchKeyword}
                onChange={(e) => setSearchKeyword(e.target.value)}
                placeholder="Search '3 BHK for sale in Mumbai'"
                className="w-full border border-gray-300 rounded-md px-3 py-2 pl-10 focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <span className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400">üîç</span>
            </div>
            <button
              type="submit"
              className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
            >
              Search
            </button>
          </form>
        </div>

        {/* Existing content */}
        <div>
          <section>
            <div className="flex justify-center items-center"></div>
            {/* Pass properties to GalleryComponent */}
            <GalleryComponent properties={properties} imgUrl="https://bestpropertiesmohali.com/assets/properties/" />
          </section>
        </div>
      </div>
    </div>
  );
}
